function validateForm() {
var total = 0;
var atLeastOneFilled = false;
for (var i = 1; i <= 5; i++) {
var name = document.querySelector('input[name="name' + i + '"]').value.trim();
var percentageStr = document.querySelector('input[name="percentage' + i + '"]').value.trim();

if (name != '' && percentageStr != '') {
atLeastOneFilled = true;
var percentage = parseFloat(percentageStr);
if (isNaN(percentage) || percentage < 0) {
alert('Le pourcentage √† la ligne #' + i + ' doit √™tre un nombre positif.');
return false;
}
total += percentage;
}
}

if (!atLeastOneFilled) {
alert('Veuillez remplir au moins une ligne avec un nom et un pourcentage.');
return false;
}

if (total != 100) {
alert('La somme des pourcentages doit √™tre exactement √©gale √† 100%.\nSomme actuelle : ' + total + '%');
return false;
}

return true;
}

document.querySelector('form').onsubmit = validateForm;
